import { useEffect, useRef, useState } from "react";
import { GoSearch } from "react-icons/go";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { searchGenAction, searchTagAction } from "../../../redux";
import SearchModal from "../../SearchModal";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0px 8px;
  flex: 1 1 auto;
  width: auto;
`;
const SearchContainer = styled.div`
  display: flex;
  align-items: center;
  padding: 7.8px 9.1px 7.8px 9.1px;
  border: 1px solid #babfc3;
  border-radius: 3px;
  width: 100%;
  background-color: white;
`;

const Input = styled.input`
  display: flex;
  align-items: center;
  border: none;
  background-color: rgba(0, 0, 0, 0);
  margin-left: 2px;
  width: 100%;
  :focus {
    outline: none;
  }
`;

function Search() {
  const [searchValue, setSearchValue] = useState("");
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [isOpen, setIsOpen] = useState(false);
  const handleOnClick = () => {
    setIsOpen(!isOpen);
  };
  const modalRef = useRef();

  const clickModalOutside = (event) => {
    if (
      isOpen &&
      (!modalRef.current || !modalRef.current.contains(event.target))
    ) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    window.addEventListener("mousedown", clickModalOutside);

    return () => {
      window.removeEventListener("mousedown", clickModalOutside);
    };
  });

  const onChangeValue = (e) => {
    setSearchValue(e.target.value);
  };

  const handleEnter = (e) => {
    if (
      e.key === "Enter" &&
      searchValue.includes("[") &&
      searchValue.includes("]")
    ) {
      dispatch(searchTagAction(searchValue.slice(1, searchValue.length - 1)));

      setIsOpen(false);
      navigate("/searchtag");
    } else if (
      e.key === "Enter" &&
      !searchValue.includes("[") &&
      searchValue.length > 0
    ) {
      dispatch(searchGenAction(searchValue));

      navigate("/search");
      setIsOpen(false);
    } else if (searchValue.length === 0) {
      return;
    }
  };

  return (
    <Container>
      <SearchContainer>
        <GoSearch color="#838C95" />
        <Input
          type="search"
          placeholder="Search..."
          onChange={onChangeValue}
          onKeyPress={handleEnter}
          onClick={handleOnClick}
          ref={modalRef}
        />
      </SearchContainer>
      {isOpen ? <SearchModal /> : null}
    </Container>
  );
}

export default Search;
